import React, { useState } from "react";
import { useParams, useLocation } from "react-router-dom";

const User = () => {
  const [stats, setShow] = useState(false);
  const { fname } = useParams();
  const location = useLocation();
  const loc = location.pathname === `/user/${fname}` ? true : false;
  const btn = () => {
    setShow(true);
    alert(loc);
    alert(stats);
  };

  return (
    <>
      <h1>hey, {fname} apka bahoot bahoot swagat hai :) </h1>
      <p> My actual url is {location.pathname} </p>
      {stats == true ? <button onClick={btn}>click me</button> : null}
    </>
  );
};

export default User;
