import React, { useState } from "react";
import { useParams, useLocation } from "react-router-dom";

const User = () => {
  const [show, setShow] = useState();
  const { fname } = useParams();
  const location = useLocation();
  show = location.pathname === `/user/${fname}` ? true : false;
  console.log(location);
  console.log(show);
  return (
    <>
      <h1>hey, {fname} apka bahoot bahoot swagat hai :) </h1>
      <p> My actual url is {location.pathname} </p>
    </>
  );
};

export default User;
